    private void btnSinFechaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSinFechaActionPerformed

        spinnerFCDia.setValue(00);
        spinnerFCMes.setValue(00);
        spinnerFCAno.setValue(000);

    }//GEN-LAST:event_btnSinFechaActionPerformed

    private void btnAgregarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAgregarActionPerformed

        String nombre = txtNombre.getText();

        if(nombre.length() > 0 ){
            String descripcion = txtDescripcion.getText();
            int dia = (int) spinnerFCDia.getValue();
            int mes = (int) spinnerFCMes.getValue();
            int año = (int) spinnerFCAno.getValue();
            String fechaCierre = ""+año+"-"+mes+"-"+dia;
            
            if(dia+año+mes > 0){
                control.guardarTarea(nombre, descripcion, fechaCierre);
            }else{
                control.guardarTarea(nombre, descripcion);
            }
            
            control.abrirPrincipal();
            this.dispose();
            
            
        }else{
            Mensajero.Mensaje("LA TAREA NECESITA UN NOMBRE", "INFO", "Requisitos para crear tarea");
            
        }

    }//GEN-LAST:event_btnAgregarActionPerformed

