    private void ajustarFechaCierre() {
        Calendar calendar = Calendar.getInstance();
        calendar.add(Calendar.DAY_OF_YEAR, 10);
        
        var dia = calendar.get(Calendar.DAY_OF_MONTH);
        int mes = calendar.get(Calendar.MONTH) + 1; // Enero es 0, por lo que sumamos 1
        int anio = calendar.get(Calendar.YEAR);

        spinnerFCDia.setValue(dia);
        spinnerFCMes.setValue(mes);
        spinnerFCAno.setValue(anio);
    }
    
